<!doctype html>
<html lang="en">
    <head>
        <meta charset="UTF-8">
        <title>ClickerHeroesApi - CodeIgniter</title>

        <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.3.5/css/bootstrap.min.css">
        <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.3.5/css/bootstrap-theme.min.css">
        <link rel="stylesheet" href="<?php echo assetUrl('css/style.css') ?>"/>
    </head>

    <body>
        <div class="wrap">
            <div class="container">
                <div class="top col-lg-12">
                    <div class="logo">
                        <img src="<?php echo assetUrl('imgs/logo.png') ?>" alt=""/>
                    </div>
                </div>

                <div class="content col-lg-12">
                    <div class="contentBox">
                        <h1>Clicker Heroes Savegame Editor</h1>
                        <p>Ben is hier</p>
                        <p>Welcome to this Savegame Editor. It's versitile, it's awesome!</p>
                        <p>Let's start this thing up!</p>
                        <button class="btn btn-default btnStart">Start</button>
                    </div>
                </div>
            </div>

            <script type="text/javascript" src="http://cdnjs.cloudflare.com/ajax/libs/jquery/2.1.4/jquery.min.js"></script>
            <script src="https://maxcdn.bootstrapcdn.com/bootstrap/3.3.5/js/bootstrap.min.js"></script>
            <script type="text/javascript">
                jQuery(document).ready(function() {
                    var ajaxUrl = "<?php echo ajaxUrl() ?>";

                    jQuery('.btnStart').click(function() {
                        jQuery.ajax({
                            url     : ajaxUrl,
                            type    : "POST",
                            data    : {
                                action : 'startIt'
                            },
                            success : function(data) {
                                //console.log(data)
                                jQuery('.content .contentBox').empty().append(data);
                            }
                        })
                    })

                    jQuery(document).on('click', '.btnDecode', function() {
                        jQuery.ajax({
                            url     : ajaxUrl,
                            type    : "POST",
                            data    : {
                                action  : 'decodeIt',
                                save    : jQuery('#savegame').val()
                            },
                            success : function(data) {
                                var obj = jQuery.parseJSON(data),
                                    str = JSON.stringify(obj, undefined, 4);
                                /*jQuery('.output').empty().append(
                                     '<pre>' + syntaxHighlight(str) + '</pre>'
                                )*/
                                jQuery('.output').empty().append(
                                    '<pre>' + syntaxHighlight(str) + '</pre>'// + '<button class="btn btn-default btnEncode">Encode</button>'
                                )
                            }
                        })
                    })

                    jQuery(document).on('click', '.btnExampleData', function() {
                        jQuery('#savegame').text('')
                    })

                    jQuery(document).on('click', '.btnEncode', function() {
                        jQuery.ajax({
                            url     : ajaxUrl,
                            type    : "POST",
                            data    : {
                                action  : 'encodeIt',
                                json    : jQuery('.output pre').text()
                            },
                            success : function(data) {
                                console.log(data)
                            }
                        })
                    })
                })
                function syntaxHighlight(json) {
                    json = json.replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;');
                    return json.replace(/("(\\u[a-zA-Z0-9]{4}|\\[^u]|[^\\"])*"(\s*:)?|\b(true|false|null)\b|-?\d+(?:\.\d*)?(?:[eE][+\-]?\d+)?)/g, function (match) {
                        var cls = 'number';
                        if (/^"/.test(match)) {
                            if (/:$/.test(match)) {
                                cls = 'key';
                            } else {
                                cls = 'string';
                            }
                        } else if (/true|false/.test(match)) {
                            cls = 'boolean';
                        } else if (/null/.test(match)) {
                            cls = 'null';
                        }
                        return '<span class="' + cls + '">' + match + '</span>';
                    });
                }
            </script>
        </div>
    </body>
</html>